mapper.select:
select 

from user u
where u.username=#{username,jdbcType=VARCHAR}

select 
distinct s.problem_id 
from solution s
where s.user_id=#{userId,jdbcType=VARCHAR} 
and s.verdict=#{verdict,jdbcType=INTEGER}

select 
distinct s.problem_id 
from solution s
where s.user_id=#{userId,jdbcType=VARCHAR} 
and s.verdict <> #{verdict,jdbcType=INTEGER}
and s.problem_id not in
(
select 
distinct s.problem_id 
from solution s
where s.user_id=#{userId,jdbcType=VARCHAR} 
and s.verdict=#{verdict,jdbcType=INTEGER}
)
